const Even={}
Even._linkToc=function(){for(let index=1;index<7;index++){const links=document.querySelectorAll('.post-details-content>h'+index);for(let i=0;i<links.length;i++){links[i].innerHTML=`<a href="#${links[i].id}" class="anchor" ><i class="fa fa-link icon-link"></i>${links[i].innerHTML}</a>`;}}};